  public cardImage: string = 'assets/cardback.png';
  public name: string = 'Liepard V';
  public fullName: string = 'Liepard V CRE';

  public wantsToShadowRipper: boolean = false;

  public reduceEffect(store: StoreLike, state: State, effect: Effect): State {
    // Ability: Hidden Claw
    // Ref: set-rebel-clash/eldegoss-v.ts (PlayPokemonEffect trigger when played from hand to bench)
    if (effect instanceof PlayPokemonEffect && effect.pokemonCard === this) {
      const player = effect.player;
      const opponent = StateUtils.getOpponent(state, player);

      if (IS_ABILITY_BLOCKED(store, state, player, this)) {
        return state;
      }

      // Collect all Pokemon with tools from both sides
      const hasPokemonWithTools = (() => {
        let found = false;
        player.forEachPokemon(PlayerType.BOTTOM_PLAYER, (cardList) => {
          if (cardList.tools.length > 0) found = true;
        });
        opponent.forEachPokemon(PlayerType.TOP_PLAYER, (cardList) => {
          if (cardList.tools.length > 0) found = true;
        });
        return found;
      })();

      if (!hasPokemonWithTools) {
        return state;
      }

      CONFIRMATION_PROMPT(store, state, player, result => {
        if (!result) return;

        // Block Pokemon without tools
        const blocked: CardTarget[] = [];
        player.forEachPokemon(PlayerType.BOTTOM_PLAYER, (cardList, _card, target) => {
          if (cardList.tools.length === 0) blocked.push(target);
        });
        opponent.forEachPokemon(PlayerType.TOP_PLAYER, (cardList, _card, target) => {
          if (cardList.tools.length === 0) blocked.push(target);
        });

        store.prompt(state, new ChoosePokemonPrompt(
          player.id,
          GameMessage.CHOOSE_POKEMON_TO_DISCARD_CARDS,
          PlayerType.ANY,
          [SlotType.ACTIVE, SlotType.BENCH],
          { allowCancel: false, min: 1, max: 1, blocked }
        ), (selected: PokemonCardList[] | null) => {
          const targets: PokemonCardList[] = selected || [];
          if (targets.length === 0) return;

          const target = targets[0];
          if (target.tools.length > 0) {
            // Find which player owns this target to discard tool to correct discard pile
            const targetOwner = StateUtils.findOwner(state, target);
            target.moveCardTo(target.tools[0], targetOwner.discard);
          }
        });
      });
    }

    // Attack 1: Shadow Ripper
    // Ref: set-chilling-reign/shaymin.ts (Sky Return - PUT_THIS_POKEMON_AND_ALL_ATTACHED_CARDS_INTO_YOUR_HAND with AfterAttackEffect)
    if (WAS_ATTACK_USED(effect, 0, this)) {
      this.wantsToShadowRipper = true;
    }

    if (effect instanceof AfterAttackEffect && this.wantsToShadowRipper) {
      this.wantsToShadowRipper = false;
      CONFIRMATION_PROMPT(store, state, effect.player, result => {
        if (result) {
          PUT_THIS_POKEMON_AND_ALL_ATTACHED_CARDS_INTO_YOUR_HAND(store, state, effect);
        }
      });
    }

    if (effect instanceof EndTurnEffect) {
      this.wantsToShadowRipper = false;
    }

    return state;
  }
