import { Card, ChooseCardsPrompt, GameMessage, StoreLike, State } from '../../game';
import { Effect } from '../../game/store/effects/effect';
import { WAS_ATTACK_USED, SHUFFLE_DECK } from '../../game/store/prefabs/prefabs';
import { PlayPokemonEffect } from '../../game/store/effects/play-card-effects';

export class Relicanth extends PokemonCard {
  public stage: Stage = Stage.BASIC;
  public cardType: CardType = W;
  public hp: number = 90;
  public weakness = [{ type: L }];
  public retreat = [C];

  public attacks = [
    {
      name: 'Fossil Search',
      cost: [C],
      damage: 0,
      text: 'Search your deck for up to 2 Rare Fossil cards and put them onto your Bench. Then, shuffle your deck.'
    },
    {
      name: 'Surf',
      cost: [W, C],
      damage: 40,
      text: ''
    }
  ];

  public regulationMark: string = 'D';
  public set: string = 'DAA';
  public setNumber: string = '40';
  public cardImage: string = 'assets/cardback.png';
  public name: string = 'Relicanth';
  public fullName: string = 'Relicanth DAA';

  public reduceEffect(store: StoreLike, state: State, effect: Effect): State {
    // Attack 1: Fossil Search
    // Ref: set-furious-fists/fossil-researcher.ts (search deck for fossils, put on bench via PlayPokemonEffect)
    if (WAS_ATTACK_USED(effect, 0, this)) {
      const player = effect.player;

      if (player.deck.cards.length === 0) {
        return state;
      }

      const slots = player.bench.filter(b => b.cards.length === 0);
      if (slots.length === 0) {
        return state;
      }

      // Block all cards that are not Rare Fossil
      const blocked: number[] = [];
      player.deck.cards.forEach((card, index) => {
        if (card.name !== 'Rare Fossil') {
          blocked.push(index);
        }
      });

      const max = Math.min(2, slots.length);

      store.prompt(state, new ChooseCardsPrompt(
        player,
        GameMessage.CHOOSE_CARD_TO_PUT_ONTO_BENCH,
        player.deck,
        {},
        { min: 0, max, allowCancel: false, blocked }
      ), selected => {
        const cards: Card[] = selected || [];
        cards.forEach((card, index) => {
          if (index < slots.length) {
            const playPokemonEffect = new PlayPokemonEffect(player, card as any, slots[index]);
            store.reduceEffect(state, playPokemonEffect);
          }
        });
        SHUFFLE_DECK(store, state, player);
      });
    }

    return state;
  }
}
