import { WAS_ATTACK_USED } from '../../game/store/prefabs/prefabs';

export class Kyurem extends PokemonCard {
  public stage: Stage = Stage.BASIC;
  public cardType: CardType = N;
  public hp: number = 120;
  public retreat = [C, C];

  public attacks = [
    {
      name: 'Extreme Freeze',
      cost: [W, W, M],
      damage: 60,
      damageCalculation: 'x' as 'x',
      text: 'Discard any amount of [W] Energy from your PokÃ©mon. This attack does 60 damage for each card you discarded in this way.'
    }
  ];

  public regulationMark: string = 'E';
  public set: string = 'EVS';
  public setNumber: string = '116';
  public cardImage: string = 'assets/cardback.png';
  public name: string = 'Kyurem';
  public fullName: string = 'Kyurem EVS';

  public reduceEffect(store: StoreLike, state: State, effect: Effect): State {
    // Attack 1: Extreme Freeze
    // Ref: set-shining-fates/manaphy.ts (CardList moveTo pattern for collecting cards)
    //      set-cosmic-eclipse/black-kyurem.ts (ChooseCardsPrompt for energy discard)
    if (WAS_ATTACK_USED(effect, 0, this)) {
      const player = effect.player;

      // Collect all W basic energy from player's active and benched Pokemon
      // We use a temp CardList to let player choose from all at once
      const waterEnergyMap: { card: EnergyCard, source: any }[] = [];
      const tempList = new CardList();

      player.forEachPokemon(PlayerType.BOTTOM_PLAYER, (cardList) => {
        cardList.cards.forEach(card => {
          if (card instanceof EnergyCard &&
              card.energyType === EnergyType.BASIC &&
              card.provides.includes(CardType.WATER)) {
            waterEnergyMap.push({ card, source: cardList });
            tempList.cards.push(card);
          }
        });
      });

      if (tempList.cards.length === 0) {
        effect.damage = 0;
        return state;
      }

      // Reset damage - will be set after player chooses
      effect.damage = 0;

      return store.prompt(state, new ChooseCardsPrompt(
        player,
        GameMessage.CHOOSE_CARD_TO_DISCARD,
        tempList,
        { superType: SuperType.ENERGY },
        { min: 0, max: tempList.cards.length, allowCancel: false }
      ), selected => {
        const cards = selected || [];
        cards.forEach(card => {
          const entry = waterEnergyMap.find(e => e.card === card);
          if (entry) {
            entry.source.moveCardTo(card, player.discard);
          }
        });
        effect.damage = 60 * cards.length;
      });
    }

    return state;
  }
}
