  public reduceEffect(store: StoreLike, state: State, effect: Effect): State {
    // Attack 1: Electrify
    // Ref: set-surging-sparks/magneton.ts (AttachEnergyPrompt - attach to bench only from deck)
    if (WAS_ATTACK_USED(effect, 0, this)) {
      const player = effect.player;

      state = store.prompt(state, new AttachEnergyPrompt(
        player.id,
        GameMessage.ATTACH_ENERGY_CARDS,
        player.deck,
        PlayerType.BOTTOM_PLAYER,
        [SlotType.BENCH],
        { superType: SuperType.ENERGY, energyType: EnergyType.BASIC, name: 'Lightning Energy' },
        { allowCancel: true, min: 0, max: 2 },
      ), transfers => {
        transfers = transfers || [];
        for (const transfer of transfers) {
          const target = StateUtils.getTarget(state, player, transfer.to);
          player.deck.moveCardTo(transfer.card, target);
        }
        SHUFFLE_DECK(store, state, player);
      });
    }

    // Attack 2: Bolt Storm
    // Ref: set-cosmic-eclipse/tropius.ts + forEachPokemon to count all L energy
    if (WAS_ATTACK_USED(effect, 1, this)) {
      const player = effect.player;

      let lightningCount = 0;
      player.forEachPokemon(PlayerType.BOTTOM_PLAYER, (cardList) => {
        const checkEnergy = new CheckProvidedEnergyEffect(player, cardList);
        store.reduceEffect(state, checkEnergy);
        checkEnergy.energyMap.forEach(em => {
          lightningCount += em.provides.filter(c => c === CardType.LIGHTNING || c === CardType.ANY).length;
        });
      });

      effect.damage += 30 * lightningCount;
    }

    return state;
  }
