  public reduceEffect(store: StoreLike, state: State, effect: Effect): State {
    // Attack 1: Energy Warp
    // Ref: set-unbroken-bonds/tentacruel.ts (MoveEnergyPrompt with TOP_PLAYER for moving opponent's energy)
    if (WAS_ATTACK_USED(effect, 0, this)) {
      const player = effect.player;
      const opponent = StateUtils.getOpponent(state, player);

      // Check if opponent has bench with energy
      let hasBenchEnergy = false;
      opponent.bench.forEach(b => {
        if (b.cards.length > 0 && b.cards.some(c => c instanceof EnergyCard)) {
          hasBenchEnergy = true;
        }
      });

      if (!hasBenchEnergy) {
        return state;
      }

      // Block active as source (only move from bench) and block bench as destination (only move to active)
      const blockedFrom: { player: PlayerType, slot: SlotType, index: number }[] = [
        { player: PlayerType.TOP_PLAYER, slot: SlotType.ACTIVE, index: 0 }
      ];
      const blockedTo: { player: PlayerType, slot: SlotType, index: number }[] = [];
      opponent.bench.forEach((_, index) => {
        blockedTo.push({ player: PlayerType.TOP_PLAYER, slot: SlotType.BENCH, index });
      });

      store.prompt(state, new MoveEnergyPrompt(
        player.id,
        GameMessage.MOVE_ENERGY_CARDS,
        PlayerType.TOP_PLAYER,
        [SlotType.BENCH, SlotType.ACTIVE],
        { superType: SuperType.ENERGY },
        { min: 1, max: 1, allowCancel: false, blockedFrom, blockedTo }
      ), result => {
        const transfers: CardTransfer[] = result || [];
        transfers.forEach(transfer => {
          const source = StateUtils.getTarget(state, player, transfer.from);
          const target = StateUtils.getTarget(state, player, transfer.to);
          source.moveCardTo(transfer.card, target);
        });
      });
    }

    // Attack 2: Psychic
    // Ref: set-guardians-rising/tapu-koko-gx.ts (CheckProvidedEnergyEffect counting energies)
    if (WAS_ATTACK_USED(effect, 1, this)) {
      const player = effect.player;
      const opponent = StateUtils.getOpponent(state, player);

      const checkEnergy = new CheckProvidedEnergyEffect(opponent, opponent.active);
      store.reduceEffect(state, checkEnergy);

      // Use reduce to count total energy provided (accounts for multi-energy cards like DCE)
      const energyCount = checkEnergy.energyMap.reduce((sum, em) => sum + em.provides.length, 0);
      effect.damage += 30 * energyCount;
    }

    return state;
  }
