  public readonly OCEAN_SEARCH_MARKER = 'MANAPHY_SHF_OCEAN_SEARCH_MARKER';

  public reduceEffect(store: StoreLike, state: State, effect: Effect): State {
    // Ability: Ocean Search
    // Ref: set-unbroken-bonds/sharpedo.ts (Greedy Evolution - top 6 cards look pattern),
    //      set-sword-and-shield/orbeetle.ts (Bug's Radar - once per turn, CardList moveTo)
    if (WAS_POWER_USED(effect, 0, this)) {
      const player = effect.player;

      if (IS_ABILITY_BLOCKED(store, state, player, this)) {
        throw new GameError(GameMessage.BLOCKED_BY_EFFECT);
      }

      // Check if this Pokemon is in the Active Spot
      const cardList = StateUtils.findCardList(state, this) as PokemonCardList;
      if (cardList !== player.active) {
        throw new GameError(GameMessage.CANNOT_USE_POWER);
      }

      if (player.deck.cards.length === 0) {
        throw new GameError(GameMessage.CANNOT_USE_POWER);
      }

      USE_ABILITY_ONCE_PER_TURN(player, this.OCEAN_SEARCH_MARKER, this);
      ABILITY_USED(player, this);

      // Look at top 6 cards
      const topCards = new CardList();
      const count = Math.min(6, player.deck.cards.length);
      player.deck.moveTo(topCards, count);

      const opponent = StateUtils.getOpponent(state, player);

      // Player chooses 1 Pokemon from top cards (optional)
      return store.prompt(state, new ChooseCardsPrompt(
        player,
        GameMessage.CHOOSE_CARD_TO_HAND,
        topCards,
        { superType: SuperType.POKEMON },
        { min: 0, max: 1, allowCancel: false }
      ), selected => {
        const cards = selected || [];

        if (cards.length > 0) {
          // Reveal the chosen Pokemon to the opponent
          SHOW_CARDS_TO_PLAYER(store, state, opponent, cards);
          // Put chosen Pokemon into hand
          topCards.moveCardsTo(cards, player.hand);
        }

        // Shuffle remaining cards back into deck
        topCards.moveTo(player.deck);
        SHUFFLE_DECK(store, state, player);
      });
    }

    REMOVE_MARKER_AT_END_OF_TURN(effect, this.OCEAN_SEARCH_MARKER, this);

    return state;
  }
