  public readonly BEA_PLAYED_MARKER = 'HITMONTOP_VIV_BEA_PLAYED_MARKER';

  public reduceEffect(store: StoreLike, state: State, effect: Effect): State {
    // Track when Bea is played from hand
    // Ref: set-champions-path/scrafty.ts (Piers played marker pattern)
    if (effect instanceof TrainerEffect
      && effect.trainerCard instanceof TrainerCard
      && effect.trainerCard.trainerType === TrainerType.SUPPORTER
      && effect.trainerCard instanceof Bea) {
      effect.player.marker.addMarker(this.BEA_PLAYED_MARKER, this);
    }

    // Attack 1: Cycle Draw
    // Ref: AGENTS-patterns.md (discard a card from hand, then draw)
    if (WAS_ATTACK_USED(effect, 0, this)) {
      const player = effect.player;

      if (player.hand.cards.length === 0) {
        return state;
      }

      store.prompt(state, new ChooseCardsPrompt(
        player,
        GameMessage.CHOOSE_CARD_TO_DISCARD,
        player.hand,
        {},
        { min: 1, max: 1, allowCancel: false }
      ), selected => {
        const cards = selected || [];
        cards.forEach(card => player.hand.moveCardTo(card, player.discard));

        if (cards.length > 0) {
          DRAW_CARDS(player, 3);
        }
      });
    }

    // Attack 2: Tornado Kick
    // Ref: set-champions-path/scrafty.ts (conditional damage based on supporter played marker)
    if (WAS_ATTACK_USED(effect, 1, this)) {
      const player = effect.player;
      if (player.marker.hasMarker(this.BEA_PLAYED_MARKER, this)) {
        effect.damage += 80;
      }
    }

    // Clean up Bea played marker at end of turn
    if (effect instanceof EndTurnEffect) {
      effect.player.marker.removeMarker(this.BEA_PLAYED_MARKER, this);
    }

    return state;
  }
